mapper.insert:
  		INSERT INTO t_ma_news (
			create_time,
			type,
			STATUS
		)
		VALUES
			(
				NOW(),
				#{type},
				#{status}
			);

mapper.update:
		update
			t_ma_news
		set
 			type = #{type},
 			status = #{status}
 		where id =#{id}

mapper.select:
SELECT * FROM t_ma_news WHERE 1 = 1
AND type = #{type}
AND STATUS != 3 LIMIT #{start},#{pageSize}
